// MainComponent.js
import React, { useEffect, useState } from 'react';
import PaymentModal from './paymentModal';
import { addPayment } from '../api/payament-service';
function AddPayment({orderIdentifier, rising}) {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [paymentRising, setPaymentRising] = useState([]);
  const [orderId, setOrderId] = useState([]);
  
  useEffect( () => {
    setPaymentRising(rising);
    setOrderId(orderIdentifier);
  })

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const handlePaymentSubmit = (orderId,paymentRising, cardId) => {

    orderId = 
    console.log('Payment Details:',orderId, paymentRising, cardId);

    addPayment(orderId, paymentRising, cardId);

    // Close the modal after submission
    closeModal();
  };

  return (
    <div>
      <h1>Your App</h1>
      <button onClick={openModal}>Open Payment Modal</button>
      <PaymentModal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        onPaymentSubmit={handlePaymentSubmit}
        amount={paymentRising}
        ID={orderId}
      />
    </div>
  );
}

export default AddPayment;
