import React, { useState } from 'react';
import './productDetails.css'; // Import your CSS file


function ProductDetails({ product }) {

  const [orderQuantity, setOrderQuantity] = useState(1); // State to capture order quantity

  const handleOrderClick = () => {
    // Open a modal to specify order quantity or use another input method
    // For this example, we'll use the window's built-in prompt function
    const quantity = window.prompt('Specify the order quantity:', orderQuantity);
    
    if (quantity !== null) {
      // Ensure quantity is a positive integer
      const parsedQuantity = parseInt(quantity, 10);
      if (!isNaN(parsedQuantity) && parsedQuantity > 0) {
        setOrderQuantity(parsedQuantity);

        // Create the order (Step 3)
        createOrder(product._id, parsedQuantity);
      }
    }
  };
  const displayOrderDetails = (order) => {
    // You can create a modal or a separate component to display order details
    // Here, we'll use a simple alert
    alert(`Order ID: ${order._id}\nProduct: ${product.title}\nQuantity: ${order.quantite}\nPayment Status: ${order.paymentStatus}`);
  };

  return (
    <div className="product-details">
      <h2 className="product-title">{product.title}</h2>
      <div className="product-description">{product.description}</div>
      <div className="product-price">${product.price}</div>
      <img className="product-image" src={product.image} alt={product.title} />
      <button onClick={handleOrderClick}>Order</button>
    </div>
  );
}

export default ProductDetails;
