import React, { useState, useEffect } from "react";

function ProductList() {
  const url = "http://localhost:3001/products/all/";
  const [data, setData] = useState([]);

  const fetchInfo = () => {
    return fetch(url)
      .then((res) => res.json())
      .then((d) => setData(d))
  }
  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="App">
      <h1 style={{ color: "green" }}>using JavaScript inbuilt FETCH API</h1>
      <center 
      st>
        {data.map((dataObj) => {
          return (
            <div
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p key={dataObj._id} style={{ fontSize: 20, color: 'white' }}>{dataObj.title}</p>
              <img src={dataObj.image} style={{ width: '200px', height: '200px' }}></img>
              <p>{dataObj.description}</p>
            </div>
          );
        })}
      </center>
    </div>
  );
}

export default ProductList;
