import React, { useState, useEffect } from 'react';
import './productDetails.css'; // Import your CSS file
import { createOrder } from '../api/order-service';
import { getAllOrders } from '../api/order-service';

function ProductDetails({ product }) {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch product details
    // ...

    // Fetch orders for the product
    fetchOrdersForProduct(product._id);
  }, [product]);

  const fetchOrdersForProduct = async (productId) => {
    try {
      console.log('productId:', productId);
      const response = await fetch(`http://localhost:5001/orders/all?productId=${productId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch orders');
      }
      const ordersData = await response.json();
      setOrders(ordersData);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setIsLoading(false);
    }
  };

  const [orderQuantity, setOrderQuantity] = useState(1); // State to capture order quantity

  const handleOrderClick = () => {
    const quantity = window.prompt('Specify the order quantity:', orderQuantity);
    
    if (quantity !== null) {
      // Ensure quantity is a positive integer
      const parsedQuantity = parseInt(quantity, 10);
      if (!isNaN(parsedQuantity) && parsedQuantity > 0) {
        setOrderQuantity(parsedQuantity);

        // Create the order (Step 3)
        createOrder(product._id, parsedQuantity);
      }
    }
  };


  return (
    <div className="product-details">

{isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
      <h2 className="product-title">{product.title}</h2>
      <div className="product-description">{product.description}</div>
      <div className="product-price">${product.price}</div>
      <img className="product-image" src={product.image} alt={product.title} />
      <button onClick={handleOrderClick}>Order</button>
      <h3>Orders for {product.title}</h3>
          <ul>
            {orders.map((order) => (
              <li key={order._id}>
                Order ID: {order._id}
                Quantity: {order.orderQuantity}
                Payment Status: {order.paymentStatus ? 'Paid' : 'Pending'}
              </li>
            ))}
          </ul>
      </>
      )}
    </div>
  );
}

export default ProductDetails;
