import axios from 'axios';

// Define the base URL for your "Product" service
const productApi = axios.create({
  baseURL: 'http://localhost:3001/products/all', // Replace with your actual service URL
});

// Fetch a list of products
export const getProducts = () => {
    return productApi.get('/all');
  };
  
  // Create a new product
  const createProduct = (productData) => {
    return productApi.post('/', productData);
  };
  
  // Update a product by ID
  const updateProduct = (productId, productData) => {
    return productApi.put(`/${productId}`, productData);
  };
  
  // Delete a product by ID
  const deleteProduct = (productId) => {
    return productApi.delete(`/${productId}`);
  };
