import React, { useEffect, useState } from 'react';
import { fetchProducts } from '../api/product_service';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts()
      .then((data) => {
        setProducts(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };
  return (
    // <ul>
    //   {products.map((product) => (
    //     <li key={product._id}>{product.title}</li>
    //   ))}
    // </ul>
    <div className="App">
      <h1 style={{ color: "green" }}>Product List</h1>
      <center
      style={{
        columnCount: 4
      }}>
        {products.map((product) => {
          return (
            <div
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p key={product._id} style={{ fontSize: 20, color: 'white' }} onClick={() => handleProductClick(product)}>{product.title}</p>
              <img src={product.image} style={{width: '200px', height: '200px'}}></img>
              
            </div>
          );
        })}
      </center>
    </div>
  ); 
}

export default ProductList;
